import React, { useState, useEffect } from "react";
import { BrowserRouter as Router, Switch, Route } from "react-router-dom";
import Welcome from "./components/pages/Welcome/Welcome";
import RequestForm from "./components/pages/Request/RequestForm";
import RideStatus from "./components/pages/RideStatus/RideStatus";
import "./App.css";
import UserRouting from "./components/pages/UserRouting";
import RingLoader from "react-spinners/RingLoader";

console.log = console.warn = console.error = () => {};

const App = (props) => {
  const [loading, setLoading] = useState(false);
  const style = {
    position: "fixed",
    top: "39%",
    left: "45%",
    transform: "translate(-50%, -50%)",
  };

  useEffect(() => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 1500);
  }, []);

  const netId = { netId: props.netId };
  const [isRegistered, setIsRegistered] = useState(false);

  const headers = {
    "api-key": props.apiKey,
  };

  useEffect(() => {
    axios
      .post("https://yalepool.com/is-registered", netId, {
        headers: headers,
      })
      .then((response) => {
        response = response.data;
        setIsRegistered(response.isRegistered);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  return (
    <div className="App">
      {loading ? (
        <div style={style}>
          <RingLoader size={150} color={"#6457FD"} loading={loading} />
        </div>
      ) : (
        <Router>
          <Switch>
            <Route path="/" exact render={() => <Welcome />} />
            <Route
              path="/home"
              exact
              render={() => (
                <UserRouting apiKey={props.api_key} netId={props.flask_token} />
              )}
            />

            <Route
              path="/request"
              exact
              render={() => (
                <RequestForm apiKey={props.api_key} netId={props.flask_token} />
              )}
            />
            <Route
              path="/ridestatus"
              exact
              render={() => (
                <RideStatus apiKey={props.api_key} netId={props.flask_token} />
              )}
            />
          </Switch>
        </Router>
      )}
    </div>
  );
};

export default App;
