import React, { useState, useEffect } from "react";
import axios from "axios";
import Dashboard from "./Dashboard/Dashboard";
import RegistrationForm from "./Register/RegistrationForm";
import RingLoader from "react-spinners/RingLoader";

const UserRouting = (props) => {
  const netId = { netId: props.netId };
  const [isRegistered, setIsRegistered] = useState(false);

  const headers = {
    "api-key": props.apiKey,
  };

  const [loading, setLoading] = useState(false);
  const style = {
    position: "fixed",
    top: "39%",
    left: "45%",
    transform: "translate(-50%, -50%)",
  };

  useEffect(() => {
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 1500);
  }, []);

  useEffect(() => {
    axios
      .post("https://yalepool.com/is-registered", netId, {
        headers: headers,
      })
      .then((response) => {
        response = response.data;
        //console.log(response.isRegistered);
        setIsRegistered(response);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  //console.log(isRegistered.isRegistered);
  return (
    <>
      {loading ? (
        <div style={style}>
          <RingLoader size={150} color={"#6457FD"} loading={loading} />
        </div>
      ) : isRegistered ? (
        <Dashboard apiKey={props.api_key} netId={props.netId} />
      ) : (
        <RegistrationForm apiKey={props.api_key} netId={props.netId} />
      )}
    </>
  );
};

export default UserRouting;
