import React, { useState, useEffect } from "react";
import axios from "axios";
import Dashboard from "./Dashboard/Dashboard";
import RegistrationForm from "./Register/RegistrationForm";
import Blank from "./blank";

const UserRouting = (props) => {
  const netId = { netId: props.netId };
  const [isRegistered, setIsRegistered] = useState(false);

  const headers = {
    "api-key": props.apiKey,
  };

  let flag = 0;

  useEffect(() => {
    axios
      .post("https://yalepool.com/is-registered", netId, {
        headers: headers,
      })
      .then((response) => {
        response = response.data;
        console.log(response.isRegistered);
        setIsRegistered(response.isRegistered);
      })
      .catch((error) => {
        console.log(error);
      });
  }, []);

  flag = isRegistered ? 1 : 2;

  return (
    <>
      (
      {flag === 0 ? (
        <Blank />
      ) : flag === 1 ? (
        <Dashboard apiKey={props.apiKey} netId={props.netId} />
      ) : (
        <RegistrationForm apiKey={props.apiKey} netId={props.netId} />
      )}
    </>
  );
};

export default UserRouting;
