          {members.map((mem) => (
            <p key={memKey++}>{mem}</p>
          ))}
        </td>
      </>
    );
  }
}

function DenyBox(props){
  let data = {'netId':props.netId, 'requestId':props.requestId}
  let header = props.header
  const [result, setResponse] = useState(null)

  function deny(e){
      e.preventDefault()
      axios.post("https://yalepool.com/cancel-request", data, {headers: header})
          .then(response => {
              setResponse(response)
          })
          .catch(error => {
              console.log(error)
          })
  }

  if(result){
      props.update()
  }
  return(
      <table>
      <tbody>
          <tr>
              <td>
                  <Button 
                      buttonSize="btn--small"
                      buttonColor="red"
                      onClick={deny}
                  >
                      Dissolve Match
                  </Button>
              </td>
          </tr>
      </tbody>
  </table>
  )
}

function DecisionBox(props) {
  let data = { netId: props.netId, requestId: props.requestId };
  let header = props.header;
  const [result, setResponse] = useState(null);

  function accept(e) {
    e.preventDefault();
    axios
      .post("https://yalepool.com/confirm-match", data, { headers: header })
      .then((response) => {
        setResponse(response);
      })
      .catch((error) => {
        console.log(error);
      });
  }

  function decline(e) {
    e.preventDefault();
    axios
      .post("https://yalepool.com/decline-match", data, { headers: header })
      .then((response) => {
        setResponse(response);
      })
      .catch((error) => {
        console.log(error);
      });
  }

  if (result) {
    props.update();
  }
  if (props.needClick) {
    return (
      <table>
        <tbody>
          <tr>
            <td>
              <Button
                buttonSize="btn--small"
                buttonColor="blue"
                onClick={accept}
              >
                Accept
              </Button>
            </td>
            <td>
              <Button
                buttonSize="btn--small"
                buttonColor="blue"
                onClick={decline}
              >
                Decline
              </Button>
            </td>
          </tr>
        </tbody>
      </table>
    );
  } else {
    return <div></div>;
  }
}

